import styled from "styled-components";

const ContainerSearch = styled.section`
    background-image: linear-gradient(90deg, #2E0854 35%, #000000 165%);
    color: #FFF;
    text-align: center;
    padding: 85px 0;
    height: 270px;
    width: 100%;
`;
const Title = styled.h2`
       color: #FFF;
        font-size: 36px;
        text-align: center;
        width: 100%;
`;
const SubTitle = styled.h3`
        font-size: 16px;
        font-weight: 500;
        margin-bottom: 40px;`;


function searchBar() {
  return (
    <ContainerSearch>
        <Title>Encontre seu pr√≥ximo livro</Title>
        <SubTitle>Escreva o nome do livro que deseja encontrar</SubTitle>
        <Input placeholder="Escreve sua proxima leitura" />
    </ContainerSearch>

  );
}

export default searchBar;
