import Input from '../input'
import styled from "styled-components";
import faceImg from './../../img/face.jpg';

const ContainerSearch = styled.section`
    background-image: linear-gradient(90deg, #000000 35%, #000000 165%);
    color: #FFF;
    text-align: center;
    padding: 85px 0;
    height: 270px;
    width: 100%;
`;
const Title = styled.h2`
       color: #FFF;
        font-size: 56px;
        text-align: center;
        width: 100%;
`;
const SubTitle = styled.h3`
        font-size: 26px;
        font-weight: 500;
        margin-bottom: 40px;`;

const FaceImage = styled.img`
    width: 100px; /* Ajuste o tamanho conforme necessário */
    height: auto;
    margin-top: 20px; /* Ajuste o espaçamento conforme necessário */
      `;

function searchBar() {
  return (
    <>
        <ContainerSearch>
            <Title>Portfólio </Title>
            <SubTitle>Thiago.s.silva</SubTitle>
            <Input placeholder="Escreve sua proxima leitura" />
            <FaceImage src={faceImg} alt="Face" />
        </ContainerSearch>

    </>

  );
}

export default searchBar;
