import './App.css';
import Logo from "./componentes/logo/index"
import profile from './img/perfil.svg'
import bag from './img/sacola.svg'

// Array with the header options
const headerText = ['HOME', 'SOBRE', 'MINHA ESTANTE', 'FAVORITOS','CARRINHO']
const headerIcons = [profile, bag]

function App() {
  return (
    <div className='App'>
      <header className='appHeader'>
      <Logo></Logo>
      <ul className='optionsHeader'>
        {/* Loop through the array and create a list item for each element */}
        { headerText.map((text)=> <li className='options'>{text}</li>) }
      </ul>

      <ul className='headerIcons'>
        {headerIcons.map((icon)=> <li><img src={icon}></img></li>)}
      </ul>

      </header>
    </div>
    );
}

export default App;
